import React, { FormEventHandler, useState } from 'react';
import { ISearshForm } from '../models/ISearch';
import { Formik, useFormik, useFormikContext } from 'formik';
import * as Yup from 'yup';

interface ISearchFormValues {
  search: string;
}

const validationSchema = Yup.object().shape({
  search: Yup.string().min(3, 'Must be min 3 symbols'),
});

export const SearchForm: React.FC<ISearshForm> = ({ onSetSubmitSearch }) => {
  //   Formik
  const initialValues: ISearchFormValues = { search: '' };
  // const { values } = useFormikContext<ISearchFormValues>();
  // const { search } = values;

  // console.log('values: ', values);
  // console.log('search: ', search);

  //   Native Search
  const [searchValue, setSearchValue] = useState<string>('');

  const onSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearchValue(event.target.value);
  };

  const handleSubmit: FormEventHandler<HTMLFormElement> = event => {
    event.preventDefault();

    onSetSubmitSearch(searchValue.trim());

    setSearchValue('');
  };

  return (
    <div>
      <form
        onSubmit={handleSubmit}
        style={{ display: 'flex', alignItems: 'center' }}
      >
        <input
          type="search"
          placeholder="Enter product name"
          value={searchValue}
          onChange={onSearchChange}
        />
        <button type="submit">Search</button>
      </form>

      <h2>Formik date</h2>
      <Formik
        initialValues={initialValues}
        onSubmit={async values => {
          await new Promise(resolve => setTimeout(resolve, 500));
          alert(JSON.stringify(values, null, 2));
        }}
        validationSchema={Yup.object().shape({
          email: Yup.string().email().required('Required'),
        })}
      >
        {({
          values,
          errors,
          touched,
          handleChange,
          handleBlur,
          handleSubmit,
          isSubmitting,
          /* and other goodies */
        }) => (
          <form onSubmit={handleSubmit}>
            <input
              type="search"
              name="search"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.search}
            />
            {errors.search && touched.search && errors.search}

            <button type="submit" disabled={isSubmitting}>
              Submit
            </button>
          </form>
        )}
      </Formik>
    </div>
  );
};
