import { AxiosInstance } from '../api/AxiosInstance';
import { IProduct } from '../models/IProducts';
import { ISearchFormValues } from '../models/ISearch';

export const getProducts = async (
  searchQuery: string | null,
  categoryQuery: string | null
) => {
  try {
    const res = await AxiosInstance.get('');

    const allProducts: IProduct[] = res.data['products'];

    if (!searchQuery && !categoryQuery) {
      return allProducts;
    }

    if (searchQuery) {
      const searchOfName = allProducts.filter(product =>
        product.name
          .toLocaleLowerCase()
          .includes(searchQuery.toLocaleLowerCase())
      );

      if (!categoryQuery) {
        return searchOfName;
      } else {
        return searchOfName.filter(product =>
          product.bsr_category.includes(categoryQuery)
        );
      }
    }
  } catch (error) {
    console.log('Error: ', (error as Error).message);
  }
};

export const getVisibleProducts = (
  products: IProduct[],
  searchData: ISearchFormValues
) => {
  const { search, category } = searchData;

  if (search.length || category.length) {
    const searchOfName = products.filter(product =>
      product.name.toLocaleLowerCase().includes(search.toLocaleLowerCase())
    );

    if (!category.length) {
      return searchOfName;
    } else {
      return searchOfName.filter(product =>
        product.bsr_category.includes(category)
      );
    }
  }

  return products;
};
