import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
// import translationEn from '../public/locales/en/translation.json';
// import translationUa from '../public/locales/ua/translation.json';

const resources = {
  en: {
    translation: {
      title: 'Our Products',
      search: 'Search',
      clear_filter: 'Clear filter',
      enter_search_text: 'Enter search text',
      select_product_category: 'Select a product category',
      go_to_amazon: 'Go to Amazon',
    },
  },
  ua: {
    translation: {
      title: 'Наші продукти',
      search: 'Пошук',
      clear_filter: 'Очистити фільтр',
      enter_search_text: 'Введіть пошуковий запит',
      select_product_category: 'Виберіть категорію',
      go_to_amazon: 'Перейти на Amazon',
    },
  },
};

i18n.use(initReactI18next).init({
  resources,
  fallbackLng: 'en',
  debug: true,
  interpolation: {
    escapeValue: false,
  },
});

export default i18n;
