import React from 'react';
import { ISearchForm, ISearchFormValues } from '../models/ISearch';
import { Formik, useFormikContext } from 'formik';
import { validationSchema } from '../servises/formValidation';

export const SearchForm: React.FC<ISearchForm> = ({ onSetSubmitSearch }) => {
  const initialValues: ISearchFormValues = { search: '' };
  // const { values, submitForm } = useFormikContext();

  const handleSubmit = (values: ISearchFormValues) => {
    const { search } = values;

    onSetSubmitSearch(search.trim());
  };

  return (
    <div>
      <Formik
        initialValues={initialValues}
        onSubmit={handleSubmit}
        validationSchema={validationSchema}
      >
        {({
          values,
          errors,
          touched,
          handleChange,
          handleBlur,
          handleSubmit,
          isSubmitting,
          /* and other goodies */
        }) => (
          <form onSubmit={handleSubmit}>
            <input
              type="search"
              name="search"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.search}
            />
            {errors.search && touched.search && errors.search}

            <button type="submit" disabled={isSubmitting}>
              Search
            </button>
          </form>
        )}
      </Formik>
    </div>
  );
};
