import React, { FormEventHandler, useState } from 'react';
import { ISearshForm } from '../models/ISearch';
import {Formik} from 'formik';

export const SearchForm: React.FC<ISearshForm> = ({ onSetSubmitSearch }) => {
  const [searchValue, setSearchValue] = useState<string>('');

  const onSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearchValue(event.target.value);
  };

  const handleSubmit: FormEventHandler<HTMLFormElement> = event => {
    event.preventDefault();

    onSetSubmitSearch(searchValue.trim());

    setSearchValue('');
  };

  return (
      <div>

        <form
            onSubmit={handleSubmit}
            style={{ display: 'flex', alignItems: 'center' }}
        >
          <input
              type="search"
              placeholder="Enter product name"
              value={searchValue}
              onChange={onSearchChange}
          />
          <button type="submit">Search</button>
        </form>

          <Formik
              initialValues={{ search: '' }}
              validate={values => {
                  const errors = {};
                  if (!values.search) {
                      errors.search = 'Required';
                  } else if (
                      !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.search)
                  ) {
                      errors.search = 'Invalid search value';
                  }
                  return errors;
              }}
              onSubmit={(values, { setSubmitting }) => {
                  setTimeout(() => {
                      alert(JSON.stringify(values, null, 2));
                      setSubmitting(false);
                  }, 400);
              }}
          >
              {({
                    values,
                    errors,
                    touched,
                    handleChange,
                    handleBlur,
                    handleSubmit,
                    isSubmitting,
                    /* and other goodies */
                }) => (
                  <form onSubmit={handleSubmit}>
                      <input
                          type="search"
                          name="search"
                          onChange={handleChange}
                          onBlur={handleBlur}
                          value={values.email}
                      />
                      {errors.email && touched.email && errors.email}
                      <input
                          type="password"
                          name="password"
                          onChange={handleChange}
                          onBlur={handleBlur}
                          value={values.password}
                      />
                      {errors.password && touched.password && errors.password}
                      <button type="submit" disabled={isSubmitting}>
                          Submit
                      </button>
                  </form>
              )}
          </Formik>

      </div>



  );
};
