import React, { FormEventHandler, useState } from 'react';
import { ISearshForm } from '../models/ISearch';
import { Formik, useFormik, useFormikContext } from 'formik';
import * as Yup from 'yup';

interface ISearchFormValues {
  search: string;
}

const validationSchema = Yup.object().shape({
  search: Yup.string().min(3, 'Must be min 3 symbols'),
});

export const SearchForm: React.FC<ISearshForm> = ({ onSetSubmitSearch }) => {
  //   Formik
  const initialValues: ISearchFormValues = { search: '' };
  // const { values } = useFormikContext<ISearchFormValues>();
  // const { search } = values;

  // console.log('values: ', values);
  // console.log('search: ', search);

  //   Native Search
  // const [searchValue, setSearchValue] = useState<string>('');

  // const onSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
  //   setSearchValue(event.target.value);
  // };

  const handleSubmit = (values: ISearchFormValues) => {
    const { search } = values;

    onSetSubmitSearch(search.trim());
  };

  return (
    <div>
      {/*<form*/}
      {/*  onSubmit={handleSubmit}*/}
      {/*  style={{ display: 'flex', alignItems: 'center' }}*/}
      {/*>*/}
      {/*  <input*/}
      {/*    type="search"*/}
      {/*    placeholder="Enter product name"*/}
      {/*    value={searchValue}*/}
      {/*    onChange={onSearchChange}*/}
      {/*  />*/}
      {/*  <button type="submit">Search</button>*/}
      {/*</form>*/}

      <Formik
        initialValues={initialValues}
        onSubmit={handleSubmit}
        validationSchema={validationSchema}
      >
        {({
          values,
          errors,
          touched,
          handleChange,
          handleBlur,
          handleSubmit,
          isSubmitting,
          /* and other goodies */
        }) => (
          <form onSubmit={handleSubmit}>
            <input
              type="search"
              name="search"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.search}
            />
            {errors.search && touched.search && errors.search}

            <button type="submit" disabled={isSubmitting}>
              Search
            </button>
          </form>
        )}
      </Formik>
    </div>
  );
};
